import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Admin.css';

const API_URL = window.location.hostname === 'localhost'
  ? 'http://localhost:8000'
  : `http://${window.location.hostname}:8000`;

axios.defaults.baseURL = API_URL;

function Admin() {
  const [stats, setStats] = useState(null);
  const [scanning, setScanning] = useState(false);
  const [scanProgress, setScanProgress] = useState(0);
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      const response = await axios.get('/api/library/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error carregant estad√≠stiques:', error);
      addLog('error', 'Error carregant estad√≠stiques');
    } finally {
      setLoading(false);
    }
  };

  const addLog = (type, message) => {
    const timestamp = new Date().toLocaleTimeString();
    setLogs(prev => [...prev, { type, message, timestamp }].slice(-50));
  };

  const handleScan = async () => {
    setScanning(true);
    setScanProgress(0);
    addLog('info', 'Iniciant escaneig de la biblioteca...');

    try {
      // Simulate progress
      const progressInterval = setInterval(() => {
        setScanProgress(prev => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return prev;
          }
          return prev + Math.random() * 15;
        });
      }, 500);

      const response = await axios.post('/api/library/scan');

      clearInterval(progressInterval);
      setScanProgress(100);

      if (response.data.status === 'success') {
        addLog('success', `Escaneig completat: ${response.data.stats?.series || 0} s√®ries, ${response.data.stats?.movies || 0} pel¬∑l√≠cules, ${response.data.stats?.files || 0} arxius`);
        await loadStats();
      } else {
        addLog('warning', 'Escaneig completat amb advert√®ncies');
      }
    } catch (error) {
      console.error('Error escanejant:', error);
      addLog('error', `Error durant l'escaneig: ${error.message}`);
    } finally {
      setScanning(false);
      setTimeout(() => setScanProgress(0), 2000);
    }
  };

  const handleRefreshMetadata = async () => {
    addLog('info', 'Actualitzant metadades...');
    try {
      await axios.post('/api/library/refresh-metadata');
      addLog('success', 'Metadades actualitzades correctament');
      await loadStats();
    } catch (error) {
      addLog('error', `Error actualitzant metadades: ${error.message}`);
    }
  };

  const handleClearCache = async () => {
    addLog('info', 'Netejant cache...');
    try {
      // Simulated - would need backend endpoint
      await new Promise(resolve => setTimeout(resolve, 1000));
      addLog('success', 'Cache netejat correctament');
    } catch (error) {
      addLog('error', `Error netejant cache: ${error.message}`);
    }
  };

  if (loading) {
    return (
      <div className="loading-screen">
        <img src="/img/hermes.png" alt="Hermes" className="loading-logo" />
        <div className="loading-text">Carregant administraci√≥...</div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h1>‚öôÔ∏è Administraci√≥</h1>
        <p>Gestiona la teva biblioteca multim√®dia Hermes</p>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üì∫</div>
          <div className="stat-info">
            <h3>{stats?.series || 0}</h3>
            <p>S√®ries</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üé¨</div>
          <div className="stat-info">
            <h3>{stats?.movies || 0}</h3>
            <p>Pel¬∑l√≠cules</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìÅ</div>
          <div className="stat-info">
            <h3>{stats?.files || 0}</h3>
            <p>Arxius</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚è±Ô∏è</div>
          <div className="stat-info">
            <h3>{stats?.total_hours || 0}h</h3>
            <p>Contingut total</p>
          </div>
        </div>
      </div>

      {/* Scanner Section */}
      <div className="admin-section">
        <div className="section-header">
          <h2>üîç Esc√†ner de Biblioteca</h2>
        </div>
        <div className="section-content">
          <div className="scanner-actions">
            <button
              className="action-btn"
              onClick={handleScan}
              disabled={scanning}
            >
              {scanning ? 'üîÑ Escanejant...' : 'üîç Escanejar biblioteca'}
            </button>

            <button
              className="action-btn secondary"
              onClick={handleRefreshMetadata}
              disabled={scanning}
            >
              üîÑ Actualitzar metadades
            </button>

            <button
              className="action-btn secondary"
              onClick={handleClearCache}
              disabled={scanning}
            >
              üóëÔ∏è Netejar cache
            </button>
          </div>

          {scanning && (
            <div className="scan-progress">
              <h4>üîÑ Escaneig en proc√©s...</h4>
              <div className="progress-bar-container">
                <div
                  className="progress-bar-fill"
                  style={{ width: `${scanProgress}%` }}
                />
              </div>
              <div className="scan-status">
                {scanProgress < 30 && 'Cercant arxius...'}
                {scanProgress >= 30 && scanProgress < 60 && 'Analitzant contingut...'}
                {scanProgress >= 60 && scanProgress < 90 && 'Actualitzant base de dades...'}
                {scanProgress >= 90 && 'Finalitzant...'}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Library Paths */}
      <div className="admin-section">
        <div className="section-header">
          <h2>üìÇ Directoris de la Biblioteca</h2>
        </div>
        <div className="section-content">
          <div className="paths-list">
            <div className="path-item">
              <div className="path-info">
                <div className="path-icon">üì∫</div>
                <div className="path-details">
                  <h4>S√®ries</h4>
                  <p>./storage/series</p>
                </div>
              </div>
              <span className="path-status active">Actiu</span>
            </div>

            <div className="path-item">
              <div className="path-info">
                <div className="path-icon">üé¨</div>
                <div className="path-details">
                  <h4>Pel¬∑l√≠cules</h4>
                  <p>./storage/movies</p>
                </div>
              </div>
              <span className="path-status active">Actiu</span>
            </div>
          </div>
        </div>
      </div>

      {/* Logs Section */}
      <div className="admin-section">
        <div className="section-header">
          <h2>üìã Registre d'Activitat</h2>
          <button
            className="action-btn secondary"
            onClick={() => setLogs([])}
            style={{ padding: '8px 15px', fontSize: '14px' }}
          >
            Netejar
          </button>
        </div>
        <div className="section-content">
          <div className="logs-container">
            {logs.length === 0 ? (
              <div style={{ color: 'rgba(255,255,255,0.4)', textAlign: 'center', padding: '20px' }}>
                No hi ha activitat recent
              </div>
            ) : (
              logs.map((log, index) => (
                <div key={index} className={`log-entry ${log.type}`}>
                  <span className="log-time">[{log.timestamp}]</span>
                  <span className="log-message">{log.message}</span>
                </div>
              ))
            )}
          </div>
        </div>
      </div>

      {/* Preferences Section */}
      <div className="admin-section">
        <div className="section-header">
          <h2>üé® Prefer√®ncies</h2>
        </div>
        <div className="section-content">
          <div className="paths-list">
            <div className="path-item">
              <div className="path-info">
                <div className="path-icon">üîä</div>
                <div className="path-details">
                  <h4>Idioma d'√†udio preferit</h4>
                  <p>Catal√† (per defecte)</p>
                </div>
              </div>
              <select
                style={{
                  padding: '8px 15px',
                  background: 'rgba(255,255,255,0.1)',
                  border: '1px solid rgba(255,255,255,0.2)',
                  borderRadius: '8px',
                  color: 'white'
                }}
                defaultValue={localStorage.getItem('hermes_audio_lang') || 'cat'}
                onChange={(e) => localStorage.setItem('hermes_audio_lang', e.target.value)}
              >
                <option value="cat">Catal√†</option>
                <option value="spa">Castell√†</option>
                <option value="eng">Angl√®s</option>
                <option value="jpn">Japon√®s</option>
              </select>
            </div>

            <div className="path-item">
              <div className="path-info">
                <div className="path-icon">üí¨</div>
                <div className="path-details">
                  <h4>Subt√≠tols per defecte</h4>
                  <p>Desactivats</p>
                </div>
              </div>
              <select
                style={{
                  padding: '8px 15px',
                  background: 'rgba(255,255,255,0.1)',
                  border: '1px solid rgba(255,255,255,0.2)',
                  borderRadius: '8px',
                  color: 'white'
                }}
                defaultValue={localStorage.getItem('hermes_subtitle_lang') || 'off'}
                onChange={(e) => localStorage.setItem('hermes_subtitle_lang', e.target.value)}
              >
                <option value="off">Desactivats</option>
                <option value="cat">Catal√†</option>
                <option value="spa">Castell√†</option>
                <option value="eng">Angl√®s</option>
              </select>
            </div>

            <div className="path-item">
              <div className="path-info">
                <div className="path-icon">‚ñ∂Ô∏è</div>
                <div className="path-details">
                  <h4>Reproducci√≥ autom√†tica</h4>
                  <p>Reproduir seg√ºent episodi autom√†ticament</p>
                </div>
              </div>
              <label style={{ display: 'flex', alignItems: 'center', gap: '10px', cursor: 'pointer' }}>
                <input
                  type="checkbox"
                  defaultChecked={localStorage.getItem('hermes_autoplay') !== 'false'}
                  onChange={(e) => localStorage.setItem('hermes_autoplay', e.target.checked)}
                  style={{ width: '20px', height: '20px', cursor: 'pointer' }}
                />
                <span style={{ color: 'rgba(255,255,255,0.7)' }}>Activat</span>
              </label>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Admin;
